import Router from 'koa-router';
import isNumber from 'is-number';
import tools from '../util/tools';
import validator from '../util/object-validator';
import userModel from '../model/User';
import { extname } from 'path';

export default new Router()
  .prefix('/common')

  .put('/update/:id', async ctx => {
    const itsValid = validator['updateBody'];
    if (!itsValid(ctx.request.body)) return ctx.badRequest({ error: 'Body is not valid' });

    const { id } = ctx.params;
    if (!isNumber(id)) return ctx.badRequest({ error: 'Id must be a number' });

    const checkUser = await userModel.getByOneField('id', id);
    if (!checkUser.length) return ctx.notFound({ error: 'User not found' });
    if (checkUser.error) return ctx.badRequest({ error: checkUser.error });

    const itsUpdate = await userModel.update(ctx.request.body, id);
    if (itsUpdate.error) return ctx.badRequest({ error: itsUpdate.error });
    if (!itsUpdate) return ctx.noContent()

    const user = await userModel.getByOneField('id', id);
    ctx.ok(user);
  })

  .post('/upload/:id', async ctx => {
    const { id } = ctx.params;
    if (!isNumber(id)) return ctx.badRequest({ error: 'Id must be a number' });

    const checkUser = await userModel.getByOneField('id', id);
    if (!checkUser.length) return ctx.notFound({ error: 'User not found' });
    if (checkUser.error) return ctx.badRequest({ error: checkUser.error });

    const { file } = ctx.request.files
    const path = process.cwd() + '/static/' + tools.getUid() + extname(file.name);
    const itsMove = await tools.moveToStatic(file.path, path);
    if (itsMove.error) return ctx.badRequest({ error: itsMove.error });

    userModel.update({ image: path }, id);
    ctx.send(202);
  })

